package com.le.No4;

public class Repetition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[] = { 7, 10, 1 };
		System.out.println("一维数组的元素为：");
		for (int x : arr) {
			System.out.println(x);
		}
		
		
		String str1 = new String("asdasdas");
		System.out.println(str1);
	}

}
